package scaler_scademy;
import java.util.*;
import java.lang.*;
public class app {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 
			int i=0;
			int sum=0;
			System.out.println(i += 1);
			while(i<100) {
				sum=sum+i;
				sum=i+sum;
				i += 1;
				
			}
		System.out.println(sum);
	}

}
